mapper.select:
select *
from(
		select rownum rn, A.*
		from(
				select ezen_notice_board.* , ezen_member.grade
				from ezen_notice_board, ezen_member
				where ((ezen_notice_board.id = ezen_member.id) and (ezen_notice_board.visible = 1))
				order by ezen_notice_board.article_num desc)A
			)
where rn between #{start} and #{end}
